css_content:
/* Dark Purple Theme for Frappe Desk */

/* Main layout colors */
.layout-main {
  background-color: #1a1a2e !important;
  color: #e6e6fa !important;
}

/* Sidebar */
.layout-side-section {
  background-color: #16213e !important;
  border-right: 1px solid #0f3460 !important;
}

/* Navbar */
.navbar {
  background-color: #0f3460 !important;
  border-bottom: 1px solid #533483 !important;
}

.navbar .navbar-brand,
.navbar .nav-link {
  color: #e6e6fa !important;
}

.navbar .nav-link:hover {
  color: #b19cd9 !important;
}

/* Buttons */
.btn-primary {
  background-color: #533483 !important;
  border-color: #533483 !important;
}

.btn-primary:hover {
  background-color: #6a4c93 !important;
  border-color: #6a4c93 !important;
}

.btn-secondary {
  background-color: #16213e !important;
  border-color: #0f3460 !important;
  color: #e6e6fa !important;
}

/* Form controls */
.form-control {
  background-color: #16213e !important;
  border-color: #0f3460 !important;
  color: #e6e6fa !important;
}

.form-control:focus {
  border-color: #533483 !important;
  box-shadow: 0 0 0 0.2rem rgba(83, 52, 131, 0.25) !important;
}

/* Cards and widgets */
.widget,
.card {
  background-color: #16213e !important;
  border-color: #0f3460 !important;
  color: #e6e6fa !important;
}

/* List items */
.list-item {
  background-color: #16213e !important;
  border-bottom: 1px solid #0f3460 !important;
}

.list-item:hover {
  background-color: #1a1a2e !important;
}

/* Module icons */
.module-icon {
  background-color: #533483 !important;
  color: white !important;
}

/* Dropdown menus */
.dropdown-menu {
  background-color: #16213e !important;
  border-color: #0f3460 !important;
}

.dropdown-item {
  color: #e6e6fa !important;
}

.dropdown-item:hover {
  background-color: #1a1a2e !important;
  color: #b19cd9 !important;
}
